mapper.select:
select

,

from education_office_message


    and message_code = #{messageCode}


    and message_title like "%"#{messageTitle}"%"


    and message_tercher_code = #{messageTercherCode}


    and message_tercher_name like "%"#{messageTercherName}"%"


    and message_state = #{messageState}




        and   message_create_time >= #{startTime}


        and  message_create_time <= #{endTime}



      and   message_create_time >= #{startTime}


      and  message_create_time <= #{endTime}
